    const prizes = [
        "7 CRORE", "1 CRORE", "₹50,00,000", "₹25,00,000", "₹12,50,000",
        "₹6,40,000", "₹3,20,000", "₹1,60,000", "₹80,000", "₹40,000",
        "₹20,000", "₹10,000", "₹5,000", "₹3,000", "₹2,000", "₹1,000"
    ];
    
    let quizQuestions = [];
    let currentQuestionIndex = 0;
    let countdown = 45;
    let timerInterval;
    
    function startQuiz() {
        quizQuestions = shuffleArray(allQuestions).slice(0, 16);
        currentQuestionIndex = 0;
        updatePrizeList();
        showQuestion();
        startCountdown();
    }
    
    function showQuestion() {
        const q = quizQuestions[currentQuestionIndex];
        document.getElementById("question").innerText = `${currentQuestionIndex + 1}. ${q.question}`;
    
        const optionsDiv = document.getElementById("options");
        optionsDiv.innerHTML = "";
    
        q.options.forEach(opt => {
            const optionEl = document.createElement("div");
            optionEl.className = "option";
            optionEl.innerHTML = `<span>${opt}</span>`;
            optionEl.onclick = () => selectOption(opt);
            if (opt === q.correctAnswer) optionEl.classList.add("correct");
            optionsDiv.appendChild(optionEl);
        });
    
        playLevelSound(currentQuestionIndex);
    
        clearInterval(timerInterval);
        if (currentQuestionIndex < 5) {
            countdown = 45;
            startCountdown();
        } else if (currentQuestionIndex < 10) {
            countdown = 60;
            startCountdown();
        } else {
            const timerElement = document.getElementById("countdownTimer");
            if (timerElement) timerElement.remove();
        }
    }
    
    function selectOption(selected) {
        if (confirm("Lock kar diya jaye?")) {
            clearInterval(timerInterval);
            const correct = quizQuestions[currentQuestionIndex].correctAnswer;
            if (selected === correct) {
                currentQuestionIndex++;
                if (currentQuestionIndex < quizQuestions.length) {
                    updatePrizeList();
                    showQuestion();
                } else {
                    window.location.href = "Quit.html";
                }
            } else {
                window.location.href = "gameover.html";
            }
        }
    }
    
    function playLevelSound(index) {
        const sound = document.getElementById(`sound${index}`);
        if (sound) {
            sound.currentTime = 0;
            sound.play();
        }
    }
    
    function updatePrizeList() {
        const prizeList = document.getElementById("prizeList");
        prizeList.innerHTML = "";
        const currentPrizeIndex = prizes.length - currentQuestionIndex - 1;
    
        let start = Math.max(0, currentPrizeIndex - 1);
        let end = Math.min(prizes.length - 1, currentPrizeIndex + 1);
    
        for (let i = start; i <= end; i++) {
            const li = document.createElement("li");
            li.textContent = prizes[i];
            if (i === currentPrizeIndex) li.classList.add("active");
            prizeList.appendChild(li);
        }
    }
    
    function startCountdown() {
        let timerElement = document.getElementById("countdownTimer");
        if (!timerElement) {
            timerElement = document.createElement("div");
            timerElement.id = "countdownTimer";
            timerElement.classList.add("timer-box");
            document.body.appendChild(timerElement);
        }
        timerElement.textContent = countdown;
        timerInterval = setInterval(() => {
            countdown--;
            timerElement.textContent = countdown;
            if (countdown < 0) {
                clearInterval(timerInterval);
                window.location.href = "gameover.html";
            }
        }, 1000);
    }
    
    function shuffleArray(arr) {
        return arr.sort(() => Math.random() - 0.5);
    }
    
    function quitGame() {
        window.location.href = "Quit.html";
    }
    
    $(document).ready(function () {
        $("#hintButton").click(function () {
            let correctOption = $(".option.correct");
            correctOption.addClass("highlight");
            setTimeout(function () {
                correctOption.removeClass("highlight");
            }, 500);
            // $(this).remove();
        });
    
        startQuiz();
    });